package Assignmentsforloops;

public class Stringconcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Helloworld";
		System.out.println(s);
		String s1="HAI";
		System.out.println(s+s1);
		int a=100;
		int b=200;
		String x="Ravi";
		String y="Pragathi";
		System.out.println(x+y);
		System.out.println(x+y+a+b);
		System.out.println(a+b+x+y);
		System.out.println(x+y+(a+b));
		System.out.println("the value of a is:"+a);
		System.out.println("the value of a is:"+b);
		System.out.println("the value of a+b is:"+(a+b));
		char p='a';
		char q='b';
		String h="hai";
		System.out.println(p+h);
		System.out.println(p+q);
		System.out.println(9/2);
		System.out.println(9.0/2);
		System.out.println(9.0/2.0);
		System.out.println(9.0/2.0);
		System.out.println(9.0/0);//INfinite
		System.out.println(0.0/2);//infinity
		//System.out.println(9/0);//AE
		//System.out.println(0/9);//AE
		System.out.println(9.0/0.0);
		//System.out.println(0/0);//AE
		System.out.println(0.0/0.0);//NAN
		System.out.println(0/0.0);//NAN
		System.out.println(0.0/0);//NAN
		System.out.println(8 % 2);
		System.out.println(9 % 2);
		
		
		
		
		
		

	}

}
