package Assignmentsforloops;

public class forcharacters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(char c = 'A'; c<='Z'; c++) {
			System.out.println(c+ " : "+ (int)c);
		}
		for(char c = 'a'; c<='z'; c++) {
			System.out.println(c+ " : "+ (int)c);

	}

}
}
